/*!50003 CREATE*/ /*!50017*/ /*!50003 TRIGGER `trigger_update_on_acc_voucher_row` AFTER UPDATE ON `acc_voucher_row` FOR EACH ROW BEGIN
	  if old.ref1 = 'folding' then
            delete from mv_folding where voucher_row_id = old.id;

            insert into mv_folding
            (entrydate,sotags,voucher_row_id,greige_production_date,folding_date,FirmId,process_reference_label,roll_no,UOM1,UOM2,units_id,type,lot_no,VoucherId,remarks,godownid,category_godownid,design_ledger_id,p1,p2,p3)
            ( select now() dt,vtt.tags as sotags, avr.id, str_to_date(concat(mvp.greigeDate,' 00:00:00'),'%d/%m/%Y %H:%i:%s') as greige_production_date, av.date as folding_date, av.firm_id as FirmId,
            SUBSTRING_INDEX(concat(group_concat(concat(c.shortcode,'-',av.serial_num) order by av.type desc),','),',', 1) as process_reference_label,
            avr.ref2 as roll_no,(avr.amount * avr.cr_dr * -1) as UOM1, 
            (if(av.type = 'Mill_Job' or av.type = 'Mill_Receive', mip.Meter, mip.meter_field) * avr.cr_dr * -1) as UOM2,
            avr.units_id as unit, av.type as type, mip.lot_no as lot_no,av.id as VoucherId, avr.remarks, 
            if(mip.godownid = '',null,mip.godownid) godownid, if(mip.category_godownid = '',null,mip.category_godownid) category_godownid, 
            avr.ref_acc design_ledger_id, if(mip.p1 = '',null,mip.p1) p1, if(mip.p2 = '',null,mip.p2) p2, mip.p3 p3
            from acc_voucher av
            join acc_voucher_book c on av.voucher_book_id = c.id
            join acc_voucher_row avr on av.id=avr.voucher_id and avr.ref1 = 'Folding'
            join mv_item_properties mip on avr.id = mip.voucher_row_id 
            left join mv_voucher_properties mvp on av.id = mvp.voucher_id
            left join voucher_row_id_to_tag vtt on vtt.voucher_row_id = avr.id
            where avr.id = old.id
            group by avr.id,avr.ref_acc, avr.ref2, mip.category_godownid, mip.godownid);
      end if;	  
  END */;;
