		if(sum(avr.amount) IS NULL,0,round(sum(avr.amount),2)) INTO totalCashPayment 
	from 
		acc_voucher av
		join acc_voucher_row avr on av.id = avr.voucher_id and avr.cr_dr = 1 and avr.units_id = 1
	where	
		date(av.date) between date(fromDate) and date(toDate) 
		and 
		av.type = 'Cash_payment'
		and av.firm_id = firm_id;
RETURN totalBankPayment + totalCashPayment;
