CREATE FUNCTION `get_current_account_analysis_sale`(firm_id BIGINT(20),
	fromDate DATETIME,
	toDate DATETIME
) RETURNS decimal(18,2)
BEGIN
	DECLARE total DECIMAL(18,2) DEFAULT 0.0;
	DECLARE totalInternal DECIMAL(18,2) DEFAULT 0.0;
	DECLARE grandTotal DECIMAL(18,2) DEFAULT 0.0;
	
	select 
		round(sum(avr.amount),2) as amount into total
	from acc_voucher_row avr
		join acc_voucher av on av.id = avr.voucher_id and avr.cr_dr = 1 and avr.units_id = 1
	where
		date(av.date) between date(fromDate) and date(toDate) 
		and av.type in (
			'Sales_With_Delievery_Challan' , 
			'Sales_without_inventory',
			'Sales',
			'Mill Outward')
		and av.id not in (select to_voucher_id from reverse_entry where to_voucher_id = av.id)
		and av.firm_id = firm_id;
		
		
	select 
		round(sum(avr.amount),2) as amount into totalInternal
	from acc_voucher_row avr
		join acc_voucher av on av.id = avr.voucher_id and avr.cr_dr = -1 and avr.units_id = 1
		join acc_ledger al on avr.ref_acc = al.id and al.name in (select name from acc_firm)
	where 
		date(av.date) between date(fromDate) and date(toDate) 
		and av.type in ('Sales_With_Delievery_Challan','Sales_without_inventory','Sales','Mill Outward')
    	and av.id not in (select to_voucher_id from reverse_entry where to_voucher_id = av.id)
		and av.firm_id = firm_id;		
	
	SET grandTotal = (IF(total IS NULL,0,total) - IF(totalInternal IS NULL,0,totalInternal));

RETURN grandTotal;
END ;;
