CREATE PROCEDURE `sp_current_account_analysis_report`(
	IN p_fromDate DATETIME,
	IN p_toDate DATETIME,
	IN p_type VARCHAR(10)
)
BEGIN
	DECLARE tempTableName VARCHAR(15) DEFAULT CONCAT('Table',replace(time(now()),':',''));
	DECLARE l_toDate DATETIME DEFAULT IF(DATEDIFF(LAST_DAY(DATE_ADD(date(p_toDate), INTERVAL 0 MONTH)),now()) > 0,now(),LAST_DAY(DATE_ADD(date(p_toDate), INTERVAL 0 MONTH)));
	DECLARE doneFirms INT DEFAULT false;
	DECLARE firmId bigint(20) DEFAULT 0;

	DECLARE weekMonth varchar(20);
	DECLARE c_fromDate DATETIME;
	DECLARE c_toDate DATETIME;

	DECLARE firmParticular varchar(100);
	DECLARE sale decimal(18,2) DEFAULT 0.0;
	DECLARE receipt decimal(18,2) DEFAULT 0.0;
	DECLARE debtors decimal(18,2) DEFAULT 0.0;
	DECLARE purchase decimal(18,2) DEFAULT 0.0;
	DECLARE payment decimal(18,2) DEFAULT 0.0;
	DECLARE creditors decimal(18,2) DEFAULT 0.0;
	DECLARE dr_cr decimal(18,2) DEFAULT 0.0;

	DECLARE curFirms CURSOR FOR SELECT id,name FROM acc_firm where status=1;
	DECLARE curMonths CURSOR FOR SELECT
		CONCAT(DATE_FORMAT(av.date,'%b'),' ',YEAR(av.date)) as Month,
		DATE_FORMAT(av.date,'%Y-%m-01') as firstDate,
		LAST_DAY(DATE_ADD(date(DATE_FORMAT(av.date,'%Y-%m-01')), INTERVAL 0 MONTH)) lastDate
	from acc_voucher av
	where
		date(av.date) 
		between 
			date(p_fromDate) 
			and date(l_toDate)
	group by Month;
	DECLARE curWeeks CURSOR FOR SELECT 
		CONCAT('Week-',WEEK(av.date)) as Week,
		IF(
			DATEDIFF(date(p_fromDate),DATE_ADD(av.date, INTERVAL(1-DAYOFWEEK(av.date)) DAY)) > 0,
			date(p_fromDate),
			DATE_ADD(av.date, INTERVAL(1-DAYOFWEEK(av.date)) DAY)
		) as fromDate,
		IF(
			DATEDIFF(DATE_ADD(av.date, INTERVAL(7-DAYOFWEEK(av.date)) DAY),date(l_toDate)) > 0,
			date(l_toDate),
			DATE_ADD(av.date, INTERVAL(7-DAYOFWEEK(av.date)) DAY)
		) as toDate
	from acc_voucher av
	where
		date(av.date) 
		between 
			date(p_fromDate) 
			and date(l_toDate)
	group by Week;
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET doneFirms = 1;
	
	DROP TABLE IF EXISTS tempTableName;
	CREATE TEMPORARY TABLE tempTableName(
		weekMonth varchar(50),
		firmParticular varchar(100),
		sale decimal(18,2),
		receipt decimal(18,2),
		debtors decimal(18,2),
		purchase decimal(18,2),
		payment decimal(18,2),
		creditors decimal(18,2),
		dr_cr decimal(18,2)
	);

	BEGIN
		OPEN curFirms;
		read_firms : LOOP
			FETCH curFirms INTO firmId,firmParticular;
			IF doneFirms THEN
				LEAVE read_firms;
			END IF;
			
			IF p_type = 'Weekly' THEN
				BEGIN
					DECLARE doneWeeks INT DEFAULT false;
					DECLARE CONTINUE HANDLER FOR NOT FOUND SET doneWeeks = true;
					OPEN curWeeks;
					read_weeks: LOOP
						FETCH curWeeks INTO weekMonth,c_fromDate,c_toDate;
						IF doneWeeks THEN
							LEAVE read_weeks;
						END IF;
						
						SET sale = get_current_account_analysis_sale(firmId,c_fromDate,c_toDate);
						SET receipt = get_current_account_analysis_receipt(firmId,c_fromDate,c_toDate);
						SET debtors = get_current_account_analysis_debtors(firmId,c_fromDate,c_toDate);
						SET purchase = get_current_account_analysis_purchase(firmId,c_fromDate,c_toDate);
						SET payment = get_current_account_analysis_payment(firmId,c_fromDate,c_toDate);
						SET creditors = get_current_account_analysis_creditors(firmId,c_fromDate,c_toDate);
						SET dr_cr = debtors - creditors;

						IF sale = 0 and receipt = 0 and debtors = 0 and purchase = 0 and payment = 0 and creditors = 0 and dr_cr = 0 THEN
							ITERATE read_weeks;
						ELSE
							INSERT INTO tempTableName
							VALUES(
								CONCAT(weekMonth,' ',DATE_FORMAT(c_fromDate,'%d %b,%Y'),' - ',DATE_FORMAT(c_toDate,'%d %b,%Y')),
								firmParticular,
								sale,
								receipt,
								debtors,
								purchase,
								payment,
								creditors,
								dr_cr);
						END IF;
					END LOOP read_weeks;
					CLOSE curWeeks;
				END;
			ELSE
				BEGIN
					DECLARE doneMonths INT DEFAULT false;
					DECLARE CONTINUE HANDLER FOR NOT FOUND SET doneMonths = true;
					OPEN curMonths;
					read_months: LOOP
						FETCH curMonths INTO weekMonth,c_fromDate,c_toDate;
						IF doneMonths THEN
							LEAVE read_months;
						END IF;
						
						SET sale = get_current_account_analysis_sale(firmId,c_fromDate,c_toDate);
						SET receipt = get_current_account_analysis_receipt(firmId,c_fromDate,c_toDate);
						SET debtors = get_current_account_analysis_debtors(firmId,c_fromDate,c_toDate);
						SET purchase = get_current_account_analysis_purchase(firmId,c_fromDate,c_toDate);
						SET payment = get_current_account_analysis_payment(firmId,c_fromDate,c_toDate);
						SET creditors = get_current_account_analysis_creditors(firmId,c_fromDate,c_toDate);
						SET dr_cr = debtors - creditors;

						IF sale = 0 and receipt = 0 and debtors = 0 and purchase = 0 and payment = 0 and creditors = 0 and dr_cr = 0 THEN
							ITERATE read_months;
						ELSE
							INSERT INTO tempTableName
							VALUES(
								weekMonth,
								firmParticular,
								sale,
								receipt,
								debtors,
								purchase,
								payment,
								creditors,
								dr_cr);
						END IF;
					END LOOP read_months;
					CLOSE curMonths;
				END;
			END IF;
		END LOOP read_firms;
		CLOSE curFirms;
	END;
	
	SELECT * FROM tempTableName;
	DROP TABLE IF EXISTS tempTableName;
END ;;
